import { cn } from "@/lib/utils";
import Link from "next/link";

export function Logo({
  variant = "",
  size,
  className,
}: {
  variant?: "" | "link";
  size?: number;
  className?: string;
}) {
  if (variant === "link") {
    return (
      <Link href={"/"} className={cn("inline-block", className)}>
        <LogoIcon />
      </Link>
    );
  }

  return (
    <div className={cn("inline-block", className)}>
      <LogoIcon size={size} />
    </div>
  );
}

