export function FullLogo({
  variant = "",
  size,
  className,
}: {
  variant?: "" | "link";
  size?: number;
  className?: string;
}) {
  if (variant === "link") {
    return (
      <Link href={"/"} className={cn("inline-block", className)}>
        <FullLogoIcon />
      </Link>
    );
  }

  return (
    <div className={cn("inline-block", className)}>
      <FullLogoIcon size={size} />
    </div>
  );
}

